import { ShopeeUIComponent } from '../../types/common';

type ShopeeAlertType = 'success'|'warning'|'info'|'error'

export class Alert extends ShopeeUIComponent {
  // descriptive text
  description: string

  // component type
  type: ShopeeAlertType

  // show icon or not
  showIcon: boolean

  // closable or not
  closable: boolean

  // customized close button text
  closeText: string

  // whether to center the text
  center: boolean
  small: boolean
}

export default Alert;
