import Vue from 'vue';
import { ShopeeUIComponent } from '../../types/common';

type ShopeeBreadcrumbTheme = 'light'|'dark'

export class Breadcrumb extends ShopeeUIComponent {
  // separator character
  separator: string

  // icon separator, accepts svg
  separatorIcon: string

  // the theme of breadcrumb
  theme: ShopeeBreadcrumbTheme

  // routing stack information of router
  // routes: Array<any>

  maxNode: number

  // routing parameters
  // params: object
}

export class BreadcrumbItem extends Vue {
  // target route of the link, same as `to` of `vue-router`
  to: string|object

  // whether use `$router.replace`, if `true`, the navigation will not leave a history record
  replace: boolean
}

export default Breadcrumb;
