import Vue from 'vue';
import { ShopeeUIComponent, ShopeeUISize } from '../../types/common';

type ButtonType = 'primary' | 'secondary';
type ButtonGroupLayout = 'vertical' | 'horizontal';

export class Button extends ShopeeUIComponent {
  // size of button
  size: ShopeeUISize

  // type of button
  type: ButtonType

  // whether is outline button
  outline: boolean

  // whether is frameless button
  frameless: boolean

  // whether is full-width button
  fullWidth: boolean

  // whether is compact button
  compact: boolean

  // dashed button
  dashed: boolean

  // ghost button
  ghost: boolean

  // whether is round button
  round: boolean

  // whether is circle button
  circle: boolean

  // whether is rich button
  rich: boolean

  // icon of button
  icon: string

  // suffix icon of button
  suffixIcon: string

  // whether the button is loading
  loading: boolean

  // whether the button is disabled
  disabled: boolean
  nativeType: string
  /**
   * render as A Tag
   */
  href: string;
  /**
   * render as A Tag
   */
  target: string;
}

export class ButtonGroup extends Vue {
  // layout of button group
  layout: ButtonGroupLayout
}

export default Button;
