import { ShopeeUIComponent, ShopeeUISize } from '../../types/common';

export class NumberInput extends ShopeeUIComponent {
  // the value of number input
  value: number

  // the placeholder of number input
  placeholder: string

  // the size of number input
  size: ShopeeUISize

  // the max value of number input
  max: number

  // the min value of number input
  min: number

  // the step of number input
  step: number

  // precision of input value
  precision: number

  // the suffix label of number input
  suffixLabel: string

  // the prefix label of number input
  prefixLabel: string

  // the prefix icon, accepts svg
  prefixIcon: string

  // whether the number input is disabled
  disabled: boolean

  // specifies the format of the value presented
  formatter: (value: number | string) => string

  // specifies the value extracted from formatter
  parser: (value: string) => number | string

  // whether the value of number input is invalid
  error: boolean

  // the error message to show when error is true
  errorMessage: string

  // trigger `change` only when enter but not when value change
  changeOnEnter: boolean
  lazy: boolean
}

export default NumberInput;
