import { ShopeeUIComponent, ShopeePopperPlacement } from '../../types/common';

type ShopeePopoverTrigger = 'click' | 'hover' | 'focus';
type ShopeePopoverTheme = 'dark' | 'light';

export class Popover extends ShopeeUIComponent {

  disabled: boolean

  // visible of popover
  value: boolean

  // placement of popover
  placement: ShopeePopperPlacement

  // trigger method of popover
  trigger: ShopeePopoverTrigger

  // placement of popover
  content: string

  // theme of popover
  theme: ShopeePopoverTheme

  // delay of popover, only active when show
  delay: number

  // max width of popover
  maxWidth: number

  // whether to hide popper when hover it
  hideHoverPopper: boolean

  // whether to hide popper when click, only effective when the `trigger` is `click`
  hideOnClick: boolean

  // whether popover is with arrow
  withArrow: boolean

  // custom class of popper
  popperClass: string

  // custom style of popper
  popperStyle: CSSStyleDeclaration

  // whether popper is flippable
  popperFlippable: boolean

  // whether popper flip is limited, flip only in one direction
  popperLimitedFlip: boolean

  // whether append popper to body
  appendToBody: boolean
}

export default Popover;
