import Vue from 'vue';
import { ShopeeUIComponent, ShopeeUISize } from '../../types/common';

type ShopeeRadioGroupLayout = 'vertical' | 'horizontal';
type ShopeeRadioGroupType = 'solid' | 'outline';

export class Radio extends ShopeeUIComponent {
  // value of radio
  value: string | number | boolean

  // name of radio
  name: string

  // whether radio is disabled
  disabled: boolean
}

export class RadioButton extends Radio {}

export class RadioGroup extends Vue {
  // value of radio
  value: string | number | boolean

  // layout of radio group
  layout: ShopeeRadioGroupLayout

  // size of radio group
  size: ShopeeUISize

  // type of raido group
  type: ShopeeRadioGroupType

  disabled: boolean
  fixWidth: number | boolean
}

export default Radio;
