import { ShopeeUIComponent } from '../../types/common';

export class Rate extends ShopeeUIComponent {
  // current value, use `v-model` to enable a two-way binding
  value: number

  // star count
  count: number

  // whether read only, unable to interact
  disabled: boolean

  // whether to allow semi selection
  allowHalf: boolean

  // whether to allow value to be any decimal, only effective when `disabled` is `true`
  allowDecimal: boolean

  // whether allow to cancel the selection
  clearable: boolean

  // whether to display a prompt text via the default slot
  showText: Boolean

  // custom icon, accepts svg
  icon: string

  // custom character
  character: string
}

export default Rate;
