import { ShopeeUIComponent } from '../../types/common';

type ShopeeScrollbarLayout = 'vertical' | 'horizontal';

export class Scrollbar extends ShopeeUIComponent {
  // whether to hide the scroll bar
  hidden: boolean

  // the layout of scroll bar
  layout: ShopeeScrollbarLayout

  // whether the scroll bar always visible when hovering over the wrapped content
  hoverVisible: boolean
}

export default Scrollbar;
