import Vue from 'vue';
import { ShopeeUIComponent } from '../../types/common';

export type ShopeeSelectUISize = 'large' | 'normal' | 'small';

export class Select extends ShopeeUIComponent {
  // value of select
  value: any

  // whether select is disabled
  disabled: boolean

  // placeholder of select
  placeholder: string

  // whether select is filterable
  filterable: boolean

  // size of select
  size: ShopeeSelectUISize

  // filter method of select
  filterMethod: (key: string, label: string) => boolean

  // filter placeholder only when filterable is true default placeholder
  filterPlaceholder: string

  // filterSuffixIcon only when filterable is true
  filterSuffixIcon: string

  // suffixIcon
  suffixIcon: string

  // clearIcon only when clearable is true
  clearIcon: string

  // whether select is clearable
  clearable: boolean

  // loadingIcon only when loading is true
  // loadingIcon: string

  // whether select is loading
  // loading: boolean

  // maxLine of selector
  maxLine: number

  // min-width of options list
  minWidth: number

  // max-width of options list
  maxWidth: number

  // max-height of options list
  maxHeight: number

  // whether append options menu to body, to be deprecated
  appendMenuToBody: boolean

  // whether append options menu to body
  appendToBody: boolean

  // popper-class of options menu
  popperClass: string

  // whether popper is flippable
  popperFlippable: boolean

  // whether multiple
  multiple: boolean

  //the max select num
  multipleLimit: number

  //whether addable
  addable: boolean

  //whether scroll to end after add
  scrollEndAfterAdd: boolean

  //search no result desc
  noMatchText: string

  //add item desc
  addItemText: string
}

export class Option extends Vue {
  // value of option
  value: any

  // label of option
  label: string

  // whether option is disabled
  disabled: boolean
}

export class OptionGroup extends Vue {
  title: string
  split: boolean
}

export default Select;
