import { ShopeeUISize, ShopeeUIComponent, ShopeePopperPlacement } from '../../types/common';

type DisabledHoursMethod = () => number[];
type DisabledMinutesMethod = (hour: number) => number[];
type DisabledSecondsMethod = (hour: number, minute: number) => number[];

export interface ShopeeTimePickerProps {
  format: string;

  disabledHours: number[] | DisabledHoursMethod;

  disabledMinutes: number[] | DisabledMinutesMethod;

  disabledSeconds: number[] | DisabledSecondsMethod;

  showDisabledTime: boolean;
}

export class TimePickerPanel extends ShopeeUIComponent {
  // the value of time picker
  value: Date

  // format of the time picker, support with `fecha`
  format: string;

  // the hours that cannot be selected
  disabledHours: number[] | DisabledHoursMethod;

  // the minutes that cannot be selected
  disabledMinutes: number[] | DisabledMinutesMethod;

  // the seconds that cannot be selected
  disabledSeconds: number[] | DisabledSecondsMethod;

  // whether show the disabled time or not
  showDisabledTime: boolean;

  // whether to show confirm button
  showConfirm: boolean;
}

export class TimePicker extends TimePickerPanel {
  // the size of time picker input
  size: ShopeeUISize

  // whether time picker is disabled
  disabled: boolean

  // whether time picker is clearable
  clearable: boolean

  // the placeholder of input
  placeholder: string

  // the placement of popper
  placement: ShopeePopperPlacement

  // custom class of popper
  popperClass: string

  // whether popper is flippable
  popperFlippable: boolean

  // whether append picker popper to body
  appendToBody: boolean
}

export default TimePicker;
