import { ShopeeUIComponent } from '../../types/common';

type ShopeeToastType = 'message' | 'warning' | 'success' | 'error';

interface ShopeeToastConfig {
  container?: Element,
  message: string
  type?: ShopeeToastType
  classNames?: string[]
  duration?: number
  maxWidth?: number
  minWidth?: number
  keepOnHover?: boolean
}

export class Toast extends ShopeeUIComponent {
  // type of toast
  type: ShopeeToastType

  // message of toast
  message: string

  // class names of toast
  classNames: string[]

  // duration of toast
  duration: number

  // max width of toast
  maxWidth: number

  // min width of toast
  minWidth: number

  // whether to keep showing when hover toast
  keepOnHover: boolean

  // static instance function
  static $instance: ShopeeToast
}

type ShopeeToastShortcutMethod = (config: ShopeeToastConfig | string) => Toast;

export declare interface ShopeeToast {
  (config: ShopeeToastConfig): Toast;
  message: ShopeeToastShortcutMethod;
  warning: ShopeeToastShortcutMethod;
  success: ShopeeToastShortcutMethod;
  error: ShopeeToastShortcutMethod;
}

export default Toast;

declare module 'vue/types/vue' {
  interface Vue {
    $toast: ShopeeToast;
  }
}
