import Vue from 'vue';
import ShopeeAnchor from '../index';
import { createInstance, destroyInstance } from '@/test/utils';

Vue.use(ShopeeAnchor);

const createAnchor = propsData => {
  return createInstance(ShopeeAnchor, propsData, true);
};

describe('anchor.vue', () => {
  let vm;
  afterEach(() => {
    destroyInstance(vm);
  });

  it('should render correct', () => {
    vm = createAnchor({});
    expect(vm.$el.classList.contains('shopee-anchor__wrapper')).to.be.true;
  });
});
