import Vue from 'vue';
import ShopeeBreadcrumb from '../index';
import { destroyInstance, createElement } from '@/test/utils';

Vue.use(ShopeeBreadcrumb);

describe('breadcrumb.vue', () => {
  let vm;
  afterEach(() => {
    destroyInstance(vm);
  });

  it('should render correct', () => {
    vm = new Vue({
      template: `<shopee-breadcrumb>
        <shopee-breadcrumb-item>Home</shopee-breadcrumb-item>
        <shopee-breadcrumb-item><a href="#">Application Center</a></shopee-breadcrumb-item>
        <shopee-breadcrumb-item><a href="#">Application List</a></shopee-breadcrumb-item>
        <shopee-breadcrumb-item>An Application</shopee-breadcrumb-item>
      </shopee-breadcrumb>`
    }).$mount(createElement());
    expect(vm.$el.classList.contains('shopee-breadcrumb')).to.be.true;
  });

});
