import Vue from 'vue';
import ShopeeButton from '../index';

Vue.use(ShopeeButton);

describe('button-group.vue', () => {
  it('should render correct layout', () => {
    const vm = new Vue({
      template: `<div>
          <shopee-button-group layout="vertical">
              <shopee-button type="primary">ShopeeButton</shopee-button>
              <shopee-button type="primary">ShopeeButton</shopee-button>
          </shopee-button-group>
      </div>`
    }).$mount();
    expect(vm.$el.querySelector('.shopee-button-group').classList.contains('vertical'))
      .to.be.true;
  });
});
