<template>
  <div :class="cardClass">
    <div :class="cardTitleClass" v-if="title && !$slots.title">{{title}}</div>
    <div :class="cardTitleClass" v-else-if="!title && $slots.title"><slot name="title"></slot></div>
    <div :class="cardContentClass"><slot></slot></div>
  </div>
</template>

<script>
const baseClass = 'shopee-card';

export default {
  name: 'ShopeeCard',
  props: {
    title: String,
    border: Boolean,
    gray: Boolean,
    hover: Boolean
  },
  computed: {
    cardClass() {
      return {
        [baseClass]: true,
        [`${baseClass}--border`]: this.border,
        [`${baseClass}--gray`]: this.gray,
        'hover': this.hover
      };
    },
    cardTitleClass() {
      return `${baseClass}__title`;
    },
    cardContentClass() {
      return `${baseClass}__content`;
    }
  }
};
</script>
