import Vue from 'vue';
import ShopeeCard from '../index';
import { createInstance, destroyInstance } from '@/test/utils';

Vue.use(ShopeeCard);

/**
 * create shopee-card instance
 * @param  {Object} propsData the propsData pass to card
 * @return {Object} the card instance
 */
const createCard = propsData => {
  return createInstance(ShopeeCard, propsData);
};

describe('card.vue', () => {
  let vm;
  afterEach(() => {
    destroyInstance(vm);
  });

  it('should render correct', () => {
    vm = createCard();

    expect(vm.$el.classList.contains('shopee-card')).to.be.true;
  });

  it('should render gray type', () => {
    vm = createCard({
      gray: true
    });

    expect(vm.$el.classList.contains('shopee-card--gray')).to.be.true;
  });

  it('should render border type', () => {
    vm = createCard({
      border: true
    });

    expect(vm.$el.classList.contains('shopee-card--border')).to.be.true;
  });
});
