import Vue from 'vue';
import { ShopeeUIComponent } from '../../types/common';

type ShopeeCascaderTrigger = 'click'|'hover'

type ShopeeCascaderSize = 'large' | 'normal' | 'small'

interface ShopeeCascaderOption {
  label: string;
  value: any;
  disabled?: boolean;
  children?: Array<ShopeeCascaderOption>;
}

export class Cascader extends ShopeeUIComponent {
  // the value of cascader
  value: Array<any>

  // the options of cascader
  options: Array<ShopeeCascaderOption>

  // option separator
  separator: string

  // the function to format select options, it will override separator if exist
  format: (options: Array<ShopeeCascaderOption>) => any

  // the placeholder of cascader
  placeholder: string

  // the way subset spreading
  trigger: ShopeeCascaderTrigger

  // whether emit change when each level selected, only valid when trigger is `click`
  changeOnSelect: boolean

  // whether the cascader is disabled
  disabled: boolean

  // the max-height of cascader
  maxHeight: number

  // the max-width of cascader menu
  menuMaxWidth: number

  // the size of cascader
  size: ShopeeCascaderSize

  // the max lines show of cascader
  maxLine: number

  // custom class of popper
  popperClass: string

  // whether popper is flippable
  popperFlippable: boolean

  // whether append popper to body
  appendToBody: boolean
}

export class CascaderPanel extends Vue {
  // the value of cascader
  value: Array<any>

  // the options of cascader
  options: Array<ShopeeCascaderOption>

  // whether emit change when each level selected, only valid when trigger is `click`
  changeOnSelect: boolean


  // the max-height of cascader
  maxHeight: number

  // the max-width of cascader menu
  menuMaxWidth: number

  // the max show of cascader menu
  maxShowLimit: number

}

export default Cascader;
