import Vue from 'vue';
import { ShopeeUIComponent, ShopeeUISize } from '../../types/common';

type CheckboxValue = string | number | boolean;
type CheckboxType = 'outline' | 'default';
type CheckboxGroupType = 'vertical' | 'horizontal';

export class Checkbox extends ShopeeUIComponent {
  // value of checkbox
  value: string | number | boolean

  // type of checkbox
  type: CheckboxType

  // name of checkbox
  name: string

  // whether checkbox is disabled
  disabled: boolean

  // whether checkbox is indeterminate
  indeterminate: boolean
}

export class CheckboxGroup extends Vue {
  // layout of checkbox group
  layout: CheckboxGroupType

  // size of checkbox group
  size: ShopeeUISize

  // whether checkbox group is disabled
  disabled: boolean
  value: CheckboxValue[]
  // fix width
  fixWidth: boolean | number
}

export class CheckboxButton extends Checkbox { }

export default Checkbox;
