import { ShopeeUIComponent } from '../../types/common';

export class Col extends ShopeeUIComponent {

  // the number of columns occupied
  span: number

  // custom element tags
  tag: string

  // the number of spaces to the left
  offset: number

  // the number of columns moved to the left
  pull: number

  // the number of columns moved to the right
  push: number
}

export default Col;
