import Vue from 'vue';
import ShopeeCol from '../index';
import { createInstance, destroyInstance } from '@/test/utils';

Vue.use(ShopeeCol);

describe('col.js', () => {
  let vm;
  afterEach(() => {
    destroyInstance(vm);
  });

  it('render col correct',() => {
    vm = createInstance(ShopeeCol);
    expect(vm.$el.classList.contains('shopee-col')).to.be.true;
  });

  it('render tag correct',() => {
    vm = createInstance(ShopeeCol, {
      tag: 'p'
    });
    expect(vm.$el.tagName).to.equals('P');
  });

  it('render push correct',() => {
    vm = createInstance(ShopeeCol,{
      push: 3
    });
    expect(vm.$el.classList.contains('shopee-col-push-3')).to.be.true;
  });

  it('render pull correct',() => {
    vm = createInstance(ShopeeCol,{
      pull: 4
    });
    expect(vm.$el.classList.contains('shopee-col-pull-4')).to.be.true;
  });

  it('render span correct',() => {
    vm = createInstance(ShopeeCol, {
      span: 5
    });
    expect(vm.$el.classList.contains('shopee-col-5')).to.be.true;
  });

  it('render offset correct',() => {
    vm = createInstance(ShopeeCol, {
      offset: 2
    });
    expect(vm.$el.classList.contains('shopee-col-offset-2')).to.be.true;
  });
});
