<template>
  <div class="shopee-default-page" :class="className">
    <shopee-icon class="shopee-default-page__icon" :class="size" :svg="icon"></shopee-icon>
    <div class="shopee-default-page__content">
      <slot></slot>
    </div>
  </div>
</template>
<script>
import { Icon as ShopeeIcon } from 'shopee-ui/lib/icon';
import noDataIcon from '@shopee-ui/icon/svg/no-data.svg';

export default {
  name: 'ShopeeDefaultPage',
  components: {
    ShopeeIcon
  },
  props: {
    icon: {
      type: String,
      default: noDataIcon
    },
    size: {
      type: String,
      default: 'normal',
      validator: value => {
        return ['normal', 'small'].includes(value);
      }
    },
    className: String
  }
};
</script>
