import Vue from 'vue';
import ShopeeDefaultPage from '../index';
import { createInstance, destroyInstance } from '@/test/utils';

Vue.use(ShopeeDefaultPage);

/**
 * create shopee-default-page instance
 * @param  {Object} propsData the propsData pass to button
 * @return {Object} the defaultPage instance
 */
const createDefaultPage = propsData => {
  return createInstance(ShopeeDefaultPage, propsData);
};

describe('default-page.vue', () => {
  let vm;
  afterEach(() => {
    destroyInstance(vm);
  });

  it('should render correct', () => {
    vm = createDefaultPage();
    expect(vm.$el.classList.contains('shopee-default-page')).to.be.true;
    expect(vm.$el.querySelector('.shopee-default-page__icon').classList.contains('normal')).to.be.true;
  });

  it('should render correct size', () => {
    vm = createDefaultPage({
      size: 'small'
    });
    expect(vm.$el.querySelector('.shopee-default-page__icon').classList.contains('small')).to.be.true;
  });
});
