import Vue from 'vue';
import { ShopeeUIComponent } from '../../types/common';

type ShopeeDropdownTriggerType = 'hover' | 'click' | 'contextMenu';
type ShopeeDropdownMenuPlacement = 'top' |
  'top-start' |
  'top-end' |
  'bottom' |
  'bottom-start' |
  'bottom-end' |
  'left' |
  'left-start' |
  'left-end' |
  'right' |
  'right-start' |
  'right-end';

export class Dropdown extends ShopeeUIComponent {
  // how to trigger dropdown menu
  trigger: ShopeeDropdownTriggerType

  // whether the dropdown menu is visible
  visible: boolean

  // whether the dropdown menu is disabled
  disabled: boolean

  // timeout before dropdown menu shown
  showTimeout: number

  // timeout before dropdown menu hide
  hideTimeout: number

  // whether hide dropdown menu when clicked
  hideOnClick: boolean
}

export class DropdownMenu extends Vue {
  // placement of dropdown menu
  placement: ShopeeDropdownMenuPlacement

  // whether the dropdown menu is full witdh
  fullWidth: boolean

  // whether the dropdown menu is fix witdh
  fixWidth: boolean

  // custom class of popper
  popperClass: string

  // whether popper is flippable
  popperFlippable: boolean

  // whether append popper to body
  appendToBody: boolean
}

export class DropdownItem extends Vue {
  // name fo item
  name: string

  // whether a divider is displayed
  divided: boolean

  // whether the item is disabled
  disabled: boolean

  // whether the item is selected
  selected: boolean

  // the prefix icon
  prefixIcon: string

  // the suffix icon
  suffixIcon: string
}

export class DropdownItemGroup extends Vue {
  // title of item group
  title: string
}

export default Dropdown;
