<template>
  <i :class="className" v-html="svg"></i>
</template>

<script>
export default {
  name: 'ShopeeIcon',
  props: {
    svg: String,
    spin: Boolean
  },
  computed: {
    className() {
      return {
        'shopee-icon': true,
        'shopee-icon-spin': this.spin
      };
    }
  }
};
</script>
