import Vue from 'vue';
import ShopeeIcon from '../index';
import { createInstance, destroyInstance } from '@/test/utils';
import SearchIcon from '@shopee-ui/icon/svg/search.svg';

Vue.use(ShopeeIcon);

describe('icon.vue', () => {
  let vm;
  afterEach(() => {
    destroyInstance(vm);
  });

  it('should render correct', () => {
    vm = createInstance(ShopeeIcon, {
      svg: SearchIcon
    });
    expect(vm.$el.classList.contains('shopee-icon')).to.be.true;
  });
});
