import Vue from 'vue';
import { ShopeeUIComponent, ShopeeUISize } from '../../types/common';

type ShopeeInputType = 'textarea' | 'text' | 'numeric';
type ShopeeInputRestrictionType = 'value' | 'input';
type RestrictionFunction = (val: string) => boolean;
type ClearFunction = () => boolean;
export class Input extends ShopeeUIComponent {
  // type of input
  type: ShopeeInputType

  // value of input
  value: string

  // placeholder of input
  placeholder: string

  // name of input
  name: string

  // whether input is readonly
  readonly: boolean

  // whether input is autofocus
  autofocus: boolean

  // whether input is autocomplete
  autocomplete: boolean

  // min length of input
  minlength: number

  // max length of input
  maxlength: number

  // size of input
  size: ShopeeUISize
  resize: 'vertical' | 'both' | 'horizontal' | 'none'

  // suffix label of input
  suffixLabel: string

  // prefix label of input
  prefixLabel: string

  // suffix icon of input
  suffixIcon: string

  // prefix icon of input
  prefixIcon: string

  // whether input is disabled
  disabled: boolean

  // whether input is clearable
  clearable: boolean
  // 默认在clearable状态下点击X会清空value，若为true则不清空
  suspendBeforeClear: boolean | ClearFunction

  // restriction of input
  restriction: string | RestrictionFunction | RegExp

  // the restriction type of input, when the type is `input`, it will only restrict the current input, when the type is `value`, it will restrict the whole value
  restrictionType: ShopeeInputRestrictionType

  // whether input is error
  error: boolean

  // error message of input
  errorMessage: string

  // minrows of input
  minrows: number

  // maxrows of input
  maxrows: number

  // whether input is autosize
  autosize: boolean

  // the same as `v-model.lazy`
  lazy: boolean

  // help message of input
  helpText: string

  // warning message of input
  warningText: string
  showWordLimit: boolean
  precision: number
  max: number
  min: number
  // wether use Math.round
  isRound: boolean

  // whether to caculate the string by utf-16, instead of the default utf-8
  unicodeNormalized: boolean
}

export class InputGroup extends Vue {
  prependWidth: number
}

export default Input;
