<template>
  <div class="shopee-input-group">
    <span class="shopee-input-group__prepend" :style="{width: `${prependWidth}px`}">
      <slot name="prepend"></slot>
    </span>
    <span class="shopee-input-group__append">
      <slot name="append"></slot>
    </span>
    <slot v-if="!$slots.prepend && !$slots.append"></slot>
  </div>
</template>

<script>
export default {
  name: 'ShopeeInputGroup',
  props: {
    prependWidth: {
      type: Number,
      default: 160
    }
  }
};
</script>
