import { ShopeeUIComponent } from '../../types/common';

type ShopeePaginationType = 'simple' | 'default' | 'increase';

export class Pagination extends ShopeeUIComponent {
  // type of pagination
  type: ShopeePaginationType

  // current page of pagination
  current: number

  // page size of pagination
  pageSize: number

  // this options of page size
  pageSizes: number[]

  // total items of pagination
  total: number

  // the amount of page
  pageCount: number

  //number of pagers
  pagerCount: number

  // whether use sizes-with-total style of size selector
  sizesWithTotal: boolean

  // layout of pagination
  layout: string

  // layout of pagination
  hasMore: boolean
}

export default Pagination;
