import { ShopeeUIComponent } from '../../types/common';

type ShopeeProgressSize = 'normal' | 'small';
type ShopeeProgressStatus = 'normal' | 'fail';

export class Progress extends ShopeeUIComponent {

  // size
  size: ShopeeProgressSize

  // the percentage of progress
  percentage: number

  // status
  status: ShopeeProgressStatus

  // whether to show text
  showText: boolean

  // the color of progress
  color: string | ((percentage: number) => string)


  // the format of text to show
  format: (percentage: number) => string
}

export default Progress;
