import { ShopeeUIComponent } from '../../types/common';

type ShopeeRowJustifyType = 'start' | 'end' | 'center' | 'space-around' | 'space-between'

type ShopeeRowAlignType = 'top' | 'middle' | 'bottom'

export class Row extends ShopeeUIComponent {

  // element tag
  tag: string

  // the interval of the column
  gutter: number

  // flex
  type: string

  // only when type is flex
  justify: ShopeeRowJustifyType

  // only when type is flex
  align: ShopeeRowAlignType
}

export default Row;
