import Vue from 'vue';
import ShopeeRow from '../index';
import { createInstance, destroyInstance } from '@/test/utils';

Vue.use(ShopeeRow);

describe('row.js', () => {
  let vm;
  afterEach(() => {
    destroyInstance(vm);
  });

  it('render row correct', () => {
    vm = createInstance(ShopeeRow);
    expect(vm.$el.classList.contains('shopee-row')).to.be.true;
  });

  it('render gutter correct',() => {
    vm = createInstance(ShopeeRow, {
      gutter: 20
    });
    expect(vm.$el.style.marginLeft).to.equals('-10px');
    expect(vm.$el.style.marginRight).to.equals('-10px');
  });

  it('render tag correct',() => {
    vm = createInstance(ShopeeRow, {
      tag: 'p'
    });
    expect(vm.$el.tagName).to.equals('P');
  });

  it('render type correct',() => {
    vm = createInstance(ShopeeRow, {
      type: 'flex'
    });
    expect(vm.$el.classList.contains('shopee-row__flex')).to.be.true;
  });

  it('render justify correct',() => {
    vm = createInstance(ShopeeRow, {
      justify: 'center'
    });
    expect(vm.$el.classList.contains('is-justify-center')).to.be.true;
  });

  it('render align correct',() => {
    vm = createInstance(ShopeeRow, {
      align: 'middle'
    });
    expect(vm.$el.classList.contains('is-align-middle')).to.be.true;
  });
});
