<template>
  <li class="shopee-sidebar-item"
    role="sidebar_item"
    tabindex="-1"
    :class="{
      'is-active': active,
    }"
    @click="handleClick"
    >
    <slot></slot>
  </li>
</template>
<script>
import { EventBusMixin } from 'shopee-ui/lib/base';
export default {
  name: 'ShopeeSidebarItem',
  mixins: [EventBusMixin],
  inject: ['root'],
  props: {
    index: String
  },
  computed: {
    active() {
      return this.index === this.root.activeIndex;
    }
  },
  methods: {
    handleClick() {
      this.$emit('click', this);
      this.dispatch('ShopeeSidebar','item-click',this);
    }
  }
};
</script>
