<template>
  <div class="shopee-skeleton"
    :class="className">
    <template v-if="type === 'line'">
      <div class="shopee-skeleton__line-first"></div>
      <div class="shopee-skeleton__line-second" v-if="!half"></div>
    </template>
    <slot></slot>
  </div>
</template>
<script>
export default {
  name: 'ShopeeSkeleton',
  props: {
    type: {
      type: String,
      default: 'line',
      validator(type = 'line') {
        return ['line', 'square', 'circle'].indexOf(type) >= 0;
      }
    },
    half: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {

    };
  },
  computed: {
    className() {
      return [
        this.type ? `shopee-skeleton__${this.type}` : 'shopee-skeleton__line'
      ];
    }
  }
};
</script>
