import { ShopeeUIComponent } from '../../types/common';

export class Slider extends ShopeeUIComponent {
  // The value of slider, use `v-model` to enable a two-way binding.
  // When range is false, it should be a number, otherwise, it should be a number array of two
  value: number | number[]

  // The minimum value the slider can slide to
  min: number

  // The maximum value the slider can slide to
  max: number

  // Tick mark of slider, key must be number and in closed interval [min, max]
  marks: { [prop: number]: any }

  // The granularity the slider can step through values, and should greater than 0.
  // When marks is not null, it can be null
  step: number

  // Whether to display breakpoints
  // When marks is not null, it is preferably false
  showStops: boolean

  // Whether to use the dual thumb mode
  range: boolean

  // Whether to disable the slider
  disabled: boolean

  // Whether is vertical mode
  vertical: boolean

  // Tooltip display controler
  showTip: 'always' | 'never' | 'hover'

  // Format the value to be displayed in tooltip, and will hide tooltip when return false
  tipFormatter: (value?: number) => void

  // Custom class of tooltip
  tipClass: string
}

export default Slider;
