import { ShopeeUIComponent } from '../../types/common';

type ShopeeSpinColor = 'orange' | 'white' | 'grey';
type ShopeeSpinSize = 'normal' | 'small';

export class Spin extends ShopeeUIComponent {
  // the color of loading icon
  color: ShopeeSpinColor

  // the size of loading icon
  size: ShopeeSpinSize

  // whether vertical center
  fix: boolean

  // whether show the mask
  mask: boolean

  // loading tips
  tips: string
}

export default Spin;
