<template>
  <div :class="className">
    <slot></slot>
  </div>
</template>
<script>
export default {
  name: 'ShopeeTag',
  props: {
    type: {
      type: String,
      validate(type = 'information') {
        return ['information', 'explain', 'promotion', 'flag', 'campaign','expired','highlight'].indexOf(type) !== -1;
      },
      default: 'information'
    },
    status: {
      type: String,
      validate(status = 'default') {
        return ['default', 'warn', 'info', 'success', 'error','invalid','return'].indexOf(status) !== -1;
      },
      default: 'default'
    },
    size: {
      type: String,
      validate(size = 'normal') {
        return ['normal', 'large'].indexOf(size) !== -1;
      },
      default: 'normal'
    }
  },
  data() {
    return {
      prefixClass: 'shopee-tag'
    };
  },
  computed: {
    className() {
      return [
        this.prefixClass,
        `${this.prefixClass}__${this.type}`,
        `${this.prefixClass}--${this.size}`,
        this.status
      ];
    }
  }
};
</script>
