import {
  NODE_KEY,
  markNodeData,
  getNodeKey,
  findNearestComponent
} from '../../src/model/util';

describe('tree/util', () => {
  it('markNodeData', () => {
    const test = {};
    markNodeData({
      id: 1
    }, test);
    expect(test[NODE_KEY]).to.eql(1);
  });

  it('getNodeKey', () => {
    const test = {
      [NODE_KEY]: 1
    };
    const result = getNodeKey(null, test);
    expect(result).to.eql(1);
  });

  it('findNearestComponent', () => {
    const dom = document.createElement('div');
    const result = findNearestComponent(dom, '');
    expect(result).to.equal(null);
  });
});
