import { ShopeeUIComponent } from '../../types/common';

export class Upload extends ShopeeUIComponent {
  // request url of upload
  action: string

  // field name of upload
  name: string

  // whether to upload multiple files
  multiple: boolean

  // headers of request
  headers: object

  // whether to send cookies
  withCredentials: boolean

  // whether to activate drag and drop mode
  drag: boolean

  // whether to support pasting upload files
  paste: boolean

  // accept file types
  accept: string

  // auto-upload
  autoUpload: boolean

  // file size limitation for each file, unit: kb
  maxSize: number

  // whether upload is disabled
  disabled: boolean
}

export default Upload;
