export default {
  computed: {
    formItem() {
      let parent = this.$parent;
      while (parent && parent.$options.name !== 'ShopeeFormItem') {
        parent = parent.$parent;
      }
      return parent;
    },
    sizeClass() {
      return this.size || (this.formItem || {}).sizeClass || 'normal';
    }
  }
};
