import { PLACEMENTS } from '../constants/placement';

export default {
  props: {
    popperClass: [String, Array],
    popperStyle: Object,
    popperFlippable: {
      type: Boolean,
      default: true
    },
    popperLimitedFlip: {
      type: Boolean,
      default: false
    },
    flipVariationsByContent: {
      type: Boolean,
      default: false
    },
    appendToBody: {
      type: Boolean,
      default: true
    },
    placement: {
      type: String,
      default: 'bottom-start',
      validator(val) {
        return PLACEMENTS.indexOf(val) !== -1;
      }
    },
    // custom popperRef
    popperRef: {}
  }
};
