import {
  getSupportedCSSProp,
  setStyles,
  getScrollParent,
  hasClass,
  addClass,
  removeClass
} from '../dom';

describe('dom.js', () => {
  it('getSupportedCSSProp', () => {
    const result = getSupportedCSSProp('position', 'sticky');
    expect(result).to.equal('sticky');
  });
  it('setStyles', () => {
    let element = document.createElement('div');
    setStyles(element, {
      display: 'none'
    });
    expect(element.style.display).to.equal('none');
  });
  it('getScrollParent', () => {
    let element = document.createElement('div');
    const parent = getScrollParent(element);
    expect(parent).to.equal(document);
  });
  it('hasClass', () => {
    let element = document.createElement('div');
    element.className = 'test';
    const result = hasClass(element,'test');
    expect(result).to.be.true;
  });
  it('addClass', () => {
    let element = document.createElement('div');
    addClass(element,'test');
    expect(element.classList.contains('test')).to.be.true;
  });
  it('removeClass', () => {
    let element = document.createElement('div');
    element.className = 'test';
    removeClass(element,'test');
    expect(element.classList.contains('test')).to.be.false;
  });
});
