<template>
  <div :class="className">
    <span class="text" :style="{maxWidth: maxTagTextWidth + 'px'}">{{text}}</span>
    <shopee-icon class="delete-icon" :svg="icon" @click.native.stop="close"></shopee-icon>
  </div>
</template>
<script>
import { Icon as ShopeeIcon } from 'shopee-ui/lib/icon';
import CLOSE_ICON from '@shopee-ui/icon/svg/close.svg';

export default {
  name: 'ShopeeSelectorTag',
  componentName: 'ShopeeSelectorTag',
  components: {
    ShopeeIcon
  },
  props: {
    text: {
      type: String,
      default: ''
    },
    maxTagTextWidth: {
      type: Number,
      default: 206
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      icon: CLOSE_ICON
    };
  },
  computed: {
    className() {
      return [this.disabled ? 'disabled' : '', 'shopee-selector--tag'];
    }
  },
  methods: {
    close() {
      this.$emit('close');
    }
  }
};
</script>
